class Parallax{constructor(imgSelector,settings){if(typeof settings==='undefined'){settings=!1}
this.img=document.querySelector(imgSelector);this.coeff=settings.coeff||2.8;this.transitionTime=settings.transitionTime||2000;this.init()}
init(){window.addEventListener('scroll',()=>{let posY=document.documentElement.scrollTop/this.coeff;this.img.style.top=`${-posY}px`})}}
class BurgerMenu{constructor(data){this.burgerIcon=document.querySelector(data.burgerSelector);this.toggleBlock=document.querySelector(data.toggleBlockSelector);this.hidePosX=data.menuHidePositionX;this.hidePosY=data.menuHidePositionY;this.showPosX=data.menuShowPositionX;this.showPosY=data.menuShowPositionY;this.trProp='all';this.trTime=this.transitionTime||1;this.trTimingFunc=this.transitionTimingFunction||'ease';this.displayVal=data.displayVal||'block';this.dir=!1;this.cross=!1;this.init()}
init(){if(this.hidePosX&&this.hidePosY&&this.showPosX&&this.showPosY){this.dir=!0}(()=>{this.toggleBlock.style.transition=`${this.trProp} ${this.trTime}s ${this.trTimingFunc}`})();const toggleMenu=(data)=>{if(!this.cross){if(!this.dir){this.toggleBlock.style.opacity=0;setTimeout(()=>{this.toggleBlock.style.display='none'},this.trTime*1000)}else{this.toggleBlock.style.transform=`translate(${this.hidePosX}, ${this.hidePosY})`}}else{if(!this.dir){this.toggleBlock.style.display=this.toggleBlockDisplayVal;setTimeout(()=>{this.toggleBlock.style.opacity=1},this.trTime)}else{this.toggleBlock.style.transform=`translate(${this.showPosX}, ${this.showPosY})`}}}
this.burgerIcon.addEventListener('click',()=>{(this.cross)?(this.burgerIcon.classList.remove('close-burger'),this.cross=!1,toggleMenu()):(this.burgerIcon.classList.add('close-burger'),this.cross=!0,toggleMenu())})
toggleMenu()}}
document.addEventListener('DOMContentLoaded',()=>{const firstParallax=new Parallax('.parallax-bg'),secondParallax=new Parallax('.footer');if(document.documentElement.clientWidth<=768){const firstBurgerMenu=new BurgerMenu({burgerSelector:'.burger',toggleBlockSelector:'.nav-menu',menuHidePositionX:'300px',menuHidePositionY:'0',menuShowPositionX:'0',menuShowPositionY:'0',toggleBlockDisplayVal:'grid'})}})
